const userValidatePassword = `-- name: UserValidatePassword :one
SELECT
    (encrypted_password = crypt($1, encrypted_password))
FROM
    user_passwords AS up
    JOIN users u ON u.id = up.user_id
        AND UPPER(u.username) = UPPER($2::text)
`

